mapper.select:
select cg.*, c.clan_code as maker_clan_code, c.clan_master_code, c.clan_name, c.clan_win, c.clan_lose, c.clan_draw, c.clan_rate, c.clan_score, c.time as maker_time 
from clan_game cg
join clan c on cg.clan_code = c.clan_code AND cg.clan_game_code = #{clan_game_code}

mapper.update:
UPDATE clan_challenger
SET state = 1
WHERE clan_game_code = #{clan_game_code} AND clan_challenger_code = #{clan_challenger_code}
