mapper.update:
UPDATE game
SET title=#{title}, loc=#{loc}, story=#{story}
WHERE game_code=#{game_code}

UPDATE challenger
SET state = 1
WHERE game_code = #{game_code} AND challenger_code = #{challenger_code}

mapper.select:
SELECT g.*, c.* FROM game g
JOIN CHALLENGER c ON g.GAME_CODE = c.GAME_CODE
WHERE c.CHALLENGER_MEMBER_CODE = #{member_code}

select count(*)
from challenger
where game_code = #{game_code} and challenger_member_code = #{member_code}

select count(*)
from game
where game_code = #{game_code} and member_code = #{member_code}
