mapper.update:
UPDATE free
SET title=#{title}, story = #{story}
WHERE free_code = #{free_code}

mapper.select:
SELECT story ,free_code, title,  hit, name, reg_date, member_code
FROM free
WHERE free_code = #{free_code}

SELECT story, free_code, title,  hit, name, reg_date
FROM FREE
WHERE free_code = #{free_code}
