mapper.update:
UPDATE free
SET title=#{title}, story = #{story}
WHERE free_code = #{free_code}

mapper.select:
SELECT free_code, title,  hit, name, reg_date, member_code,story 
FROM free
WHERE free_code = #{free_code}

SELECT free_code, title,  hit, name, reg_date, story
FROM FREE
WHERE free_code = #{free_code}
