mapper.update:
UPDATE clan
SET clan_name = #{clan_name}
WHERE clan_master = #{member_code}

mapper.select:
SELECT clan_name
FROM clan
WHERE clan_code = #{clan_code}

mapper.update:
update member
SET clan_code  = #{clan_code}
WHERE member_code = #{member_code}

update clan_member
set state = 1
where clan_member_code = #{clan_member_code} and clan_code = #{clan_code}

update member
SET clan_code = #{clan_code}
WHERE member_code = #{member_code}
