mapper.update:
UPDATE suggest_master
SET title=#{title}, story = #{story}
WHERE master_code = #{masterCode}

mapper.select:
SELECT master_code, title,  hit, name, reg_date, member_code ,story 
 FROM suggest_master
WHERE master_code = #{masterCode}

SELECT  master_code, title,  hit, name, reg_date, story
FROM suggest_master
WHERE master_code = #{masterCode}
